import { Link } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { getProjects,deleteProject } from '../api'; 

function TaskListPage() {
  const [projects, setProjects] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const projectsData = await getProjects();
        setProjects(projectsData);
      } 
      catch (err) {
        setError(`Error fetching projects: ${err.message}`);
      }
    };

    fetchProjects();
  }, []);

  const deleteProjects = async (id) => {
    try {
      await deleteProject(id);
      setProjects(projects.filter((project) => project.id !== id));
    } 
    catch (err) {
      setError(`Error deleting project: ${err.message}`);
    }
  };

  const cutOffText = (text, maxLength) => {
    if (text.length > maxLength) {
      return text.substring(0, maxLength) + '...';
    }
    return text;
  };

  return (
    <div id="root">

      <div className="container-custom mt-3">
        <h1 className=''>
          Tasks
        </h1>

        <div className="mt-4">
          <Link to="/add-task">
            <button className="btn btn-primary">Add Task</button>
          </Link>
        </div>

        {error && <p className="text-danger">{error}</p>}

        {projects.length > 0 ? (
          <table className="table table-striped mt-4">
            <tbody>
              {projects.map((project) => (
                <tr key={project.id}>
                  <td>
                    <div className='fw-bold fs-4'>
                      {cutOffText(project.title, 30)}
                      {<button className="btn btn-success ms-3">Status: {project.status}</button>}
                    </div>
                    <div>{cutOffText(project.description, 110)}</div>
                  </td>
                  <td className="text-end">
                    <Link to={`/edit-task/${project.id}`}>
                      <button className="btn btn-secondary me-2">Edit</button>
                    </Link>
                    <button
                      className="btn btn-danger me-2"
                      onClick={() => deleteProjects(project.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No projects available.</p>
        )}
      </div>

    </div>
  );  
}

export default TaskListPage;
