import { Link } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';
import Header from './header';

function TaskListPage() {
  const [tasks, setTasks] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const token = localStorage.getItem('token'); // Get the token from localStorage

        if (!token) {
          throw new Error('No token found, please login again.');
        }

        // Send the token in the Authorization header
        const response = await axios.get('http://localhost:4000/api/tasks', {
          headers: { Authorization: `Bearer ${token}` }
        });

        setTasks(response.data);
      } 
      catch (err) {
        setError(`Error fetching projects: ${err.response?.data?.error || err.message}`);
      }
    };

    fetchTasks();
  }, []);

  const deleteProjects = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/api/tasks/${id}`);
      setTasks(tasks.filter(task => task.id !== id));
    } 
    catch (err) {
      setError(`Error deleting project: ${err.message}`);
    }
  };

  const cutOffText = (text, maxLength) => {
    if (text.length > maxLength) {
      return text.substring(0, maxLength) + '...';
    }
    return text;
  };

  return (
    <div id="tasks">
      <Header />

      <div className="container-custom mt-3">
        <h1 className=''>
          User Profile
        </h1>

        {error && <p className="text-danger">{error}</p>}

        {tasks.length > 0 ? (
          <table className="table table-striped mt-4">
            <tbody>
              {tasks.map((task) => (
                <tr key={task.id}>
                  <td>
                    <div className='fw-bold fs-4'>
                      {cutOffText(task.title, 30)}
                      {<button className="btn btn-success ms-3">Status: {task.status}</button>}
                    </div>
                    <div>{cutOffText(task.description, 110)}</div>
                  </td>
                  <td className="text-end">
                    <Link to={`/edit-task/${task.id}`}>
                      <button className="btn btn-secondary me-2">Edit</button>
                    </Link>
                    <button
                      className="btn btn-danger me-2"
                      onClick={() => deleteProjects(task.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No tasks available.</p>
        )}
      </div>

    </div>
  );  
}

export default TaskListPage;
